file_paths &lt;- c("Justine.docx", "Balthazar.docx", 
                "Mountolive.docx","Clea.docx")
names &lt;- gsub(pattern = "\\.docx$", replacement = "", x = file_paths)

texts &lt;- list()
for (doc in file_paths) {
  texts[[doc]] &lt;- readtext(doc)$text
}


all_books &lt;- tibble(
  –ö–Ω–∏–≥–∞ = names[], 
  –¢–µ–∫—Å—Ç = unlist(texts))

unify_quotes &lt;- function(text) {
  text &lt;- str_replace_all(text, "[\u201C\u201D\u2018\u2019\u00AB\u00BB\"]", "\"")
  return(text)
}

delete_footnotes &lt;- function(text) {
  text &lt;- str_replace_all(text, "(?s)(?i)–ü—Ä–∏–º–µ—á–∞–Ω–∏—è.*$", "")
  return(text)
}

delete_novel_info &lt;- function(text) {
  text &lt;- str_replace_all (text, "(?i)(–õ–æ—Ä–µ–Ω—Å –î–∞—Ä—Ä–µ–ª–ª|–ñ—é—Å—Ç–∏–Ω|–ë–∞–ª—å—Ç–∞–∑–∞—Ä|–ú–∞—É–Ω—Ç–æ–ª–∏–≤|–ö–ª–µ–∞|–ê–ª–µ–∫—Å–∞–Ω–¥—Ä–∏–π—Å–∫–∏–π –∫–≤–∞—Ä—Ç–µ—Ç)", "")
  return(text)
}

all_books &lt;- all_books |&gt;
  mutate(–¢–µ–∫—Å—Ç = unify_quotes(–¢–µ–∫—Å—Ç)) |&gt;
  mutate(–¢–µ–∫—Å—Ç = delete_footnotes(–¢–µ–∫—Å—Ç)) |&gt;
  mutate(–¢–µ–∫—Å—Ç = delete_novel_info(–¢–µ–∫—Å—Ç))

